import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import TextField from '@material-ui/core/TextField';
import ReactScrollbar from 'react-scrollbar';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import { Button } from '@material-ui/core';
import * as firebase from 'firebase'

// Initialize Firebase
var config = {
  apiKey: "AIzaSyBYi0HSbsFNdlWoDfs4xyz5D7J9wIOlB7o",
  authDomain: "messagingapp-6247a.firebaseapp.com",
  databaseURL: "https://messagingapp-6247a.firebaseio.com",
  projectId: "messagingapp-6247a",
  storageBucket: "messagingapp-6247a.appspot.com",
  messagingSenderId: "176353794667"
};
firebase.initializeApp(config);

var database = firebase.database();

window.firebase = firebase

class ChatRoom extends Component {

  constructor(props) {
    super(props);

    this.state = {
      inputValue1: "",
      inputValue2: "",
      reactID: "0.03",
      messages: [
        {
          text: "HEllO WORLD",
          accNumber: 1
        },
        {
          text: "another message",
          accNumber: 2
        }
      ]
    };
  }

  addMessage1() {
    // state.messages.push()
    this.setState({
      messages: this.state.messages.concat([{text: this.state.inputValue1, accNumber: 1}])
    })
  }
  addMessage2() {
    this.setState({
      messages: this.state.messages.concat([{text: this.state.inputValue2, accNumber: 2}])
    })
  }
  switchToSignIn(){
    this.setState({reactID: "0.01"})
  }


  render() {

    var humanStyle = {
      backgroundColor: "#66FFFF",
      marginLeft: "140px",
      marginTop: "2px",
      marginRight: "10px"
    }
    var humanStyle2 = {
      backgroundColor: "red",
      marginRight: "140px",
      marginRight: "140px",
      marginTop: "10px"
    }



    return (
      <div className="ChatRoom">
        <header className="App-header">
        <h7>Welcome user, <a onclick="switchToSignIn()" href="#" >Log Out</a></h7>
          <p>Messaging Board</p>
          <Grid className="Messaging-Board" item xs = {5}>

            <ReactScrollbar speed={0.8} className="scroll" horizontal={false}>
              {this.state.messages.map(message => {

                  if(message.accNumber==1) return <div style={humanStyle} >{message.text}</div>
                  else return <div style={humanStyle2} >{message.text}</div>

                })} 
            </ReactScrollbar>
          </Grid>
          <Grid className = "one" item xs = {4}>
              <label>Human 1</label>
              <TextField value={this.state.value} onChange={event => this.setState({ inputValue1: event.target.value })}> </TextField>
              <button type="submit" onClick={() => this.addMessage1()}>send</button>
          </Grid>

        </header>
      </div>
    );
  }
}

var x = Math.floor(Math.random() * (max-min) + min)



class App extends Component {
  constructor(props){
    super(props);

    this.state = {
      name: "",
      picURL: "",
      email: "",
      password: "",
      reactID: "0.01",
      counter: "0",
    };
  }

  createAccount(name, picURL, email, password) {
    firebase.database().ref('user1/' + x).set({
      name: name,
      picURL: picURL,
      email: email,
      password: password,
    });
  }
  writeUserData1(name, picURL, email, password, reactID) {
    firebase.database().ref('user/' + x).set({
      name: name,
      picURL: picURL,
      email: email,
      password: password,
    });
  }

  switchToSignUp(){
    this.setState({reactID: "0.02"})
  }
  switchToSignIn(){
    this.setState({reactID: "0.01"})
  }
  validateAndTransition(){
    this.setState({reactID: "0.03"})
  }
  render(){

    var styleObject1 = {
      marginLeft: "34.3%"
    }
    var styleObject2 = {
      marginLeft: "33.5%",
      fontSize: "45px"
    }
    var styleObject3 = {
      marginLeft: "45%",
    }

   if (this.state.reactID == 0.01 || this.state.reactID == 0.02) {

   }
   if (this.state.reactID == 0.01) {
    var x;

    x=(
      <div className="Form">

               <Grid item xs={12}><h1 style={styleObject2}>Sign In!</h1></Grid>
               <Grid item xs={12} > <TextField  required id="standard-required" label="Required" helperText="Username" style = {styleObject1} margin="normal" value={this.state.value} onChange={event => this.setState({ name: event.target.value })}> </TextField> </Grid>
               <Grid item xs={12}>
               <TextField required
               id="standard-required"
               label="Required"
               helperText="Password"
               style = {styleObject1}
               margin="normal" value={this.state.value} onChange={event => this.setState({ password: event.target.value })}> </TextField>
             </Grid>

              <Grid item xs={12}style={styleObject3}>
               <button type="submit"onClick={() => this.validateAndTransition()}>Sign In!</button>
              </Grid>
              <Grid item xs={12} style = {styleObject3}> <button type="submit" onClick={() => this.switchToSignUp()}>Sign Up!</button>
              </Grid>
              </div>
    )
   }



   if (this.state.reactID == 0.02) {

    var x;

    x=(
        <div className="Form">
               <Grid item xs={12}><h1 style={styleObject2}>Sign Up!</h1></Grid>
               <Grid item xs={12} > <TextField  required id="standard-required" label="Required" helperText="Username" style = {styleObject1} margin="normal" value={this.state.value} onChange={event => this.setState({ name: event.target.value })}> </TextField> </Grid> 
               <Grid item xs={12}>
               <TextField required
               id="standard-required"
               label="Required"
               helperText="Password"
               style = {styleObject1}
               margin="normal" value={this.state.value} onChange={event => this.setState({ password: event.target.value })}> </TextField>
             </Grid>
             <Grid item xs={12}>
             <TextField required 
             id="standard-required"
             label="Required"
             helperText="email"
             style = {styleObject1}
             margin="normal" value={this.state.value} onChange={event => this.setState({ picURL: event.target.value })}> </TextField>
             </Grid>
             <Grid item xs={12}>
             <TextField required
             id="standard-required"
             label="Required"
             helperText="profile picture URL"
             style = {styleObject1}
             margin="normal" value={this.state.value} onChange={event => this.setState({ email: event.target.value })}> </TextField>
             <Grid item xs={12} style = {styleObject3}> <button type="submit" onClick={() => this.createAccount(this.state.name, this.state.picURL, this.state.email, this.state.password)}>Sign Up!</button>
            </Grid>
           </Grid>
             <Grid item xs={12}style={styleObject3}>
             <button type="submit"onClick={() => this.switchToSignIn()}>Sign In!</button>
            </Grid>

            </div>
     )
   }
   if (this.state.reactID == 0.03) {
        var chatRoom = <ChatRoom></ChatRoom>      
   }





    return(
      <div>
        {x}{chatRoom}
     </div>

    )
  }
}
export default App;
